import React, { useEffect, useState } from "react";

function ProductList() {
  const [products, setProducts] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const itemsPerPage = 4;
  // This assumes your FastAPI server is running locally at port 8000
  const API_URL = "http://127.0.0.1:8080/products";

  useEffect(() => {
    fetch(API_URL)
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.error("Error fetching products:", err));
  }, []);

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => 
      prevIndex + itemsPerPage >= products.length ? 0 : prevIndex + itemsPerPage
    );
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => 
      prevIndex - itemsPerPage < 0 ? Math.max(0, products.length - itemsPerPage) : prevIndex - itemsPerPage
    );
  };

  return (
    <div style={styles.sliderContainer}>
      <button 
        onClick={prevSlide} 
        style={styles.navButton}
        disabled={currentIndex === 0}
      >
        ←
      </button>
      
      <div style={styles.gridContainer}>
        {products
          .slice(currentIndex, currentIndex + itemsPerPage)
          .map((product) => (
            <ProductCard key={product.name} product={product} />
          ))}
      </div>

      <button 
        onClick={nextSlide} 
        style={styles.navButton}
        disabled={currentIndex + itemsPerPage >= products.length}
      >
        →
      </button>
    </div>
  );
}

function ProductCard({ product }) {
  // A local state to track which color is selected
  // Adjust to your actual color keys if your JSON uses "red", "blue", "green", etc.
  const colors = [
    { label: "Yellow Gold", value: "yellow", hex: "#E6CA97" },
    { label: "White Gold", value: "white", hex: "#D9D9D9" },
    { label: "Rose Gold", value: "rose", hex: "#E1A4A9" }
  ];
  const [selectedColor, setSelectedColor] = useState(colors[0]);

  const renderStars = (score) => {
    const fullStars = Math.floor(score);
    const hasHalfStar = score % 1 >= 0.5;
    const emptyStars = 5 - Math.ceil(score);
    
    return (
      <div style={styles.starsContainer}>
        {[...Array(fullStars)].map((_, i) => (
          <img
            key={`full-${i}`}
            src="https://raw.githubusercontent.com/FortAwesome/Font-Awesome/master/svgs/solid/star.svg"
            alt="full star"
            style={styles.star}
          />
        ))}
        {hasHalfStar && (
          <img
            src="https://raw.githubusercontent.com/FortAwesome/Font-Awesome/master/svgs/solid/star-half.svg"
            alt="half star"
            style={styles.star}
          />
        )}
        {[...Array(emptyStars)].map((_, i) => (
          <img
            key={`empty-${i}`}
            src="https://raw.githubusercontent.com/FortAwesome/Font-Awesome/master/svgs/regular/star.svg"
            alt="empty star"
            style={styles.star}
          />
        ))}
      </div>
    );
  };

  // Attempt to map the product images based on color, if your JSON has corresponding keys
  // If you have images = { yellow: "...", white: "...", rose: "..." }, then do something like:
  // const productImage = product.images[selectedColor.value];
  // For simplicity, if your JSON is "red/blue/green", adapt the color array to match.
  const productImage = product.images?.[selectedColor.value] 
                        || Object.values(product.images || {})[0]; // fallback if color key not found

  return (
    <div style={styles.card}>
      <img 
        src={productImage || "https://via.placeholder.com/200"} 
        alt={product.name} 
        style={styles.image} 
      />

      <div style={styles.info}>
        <h2 style={styles.title}>{product.name || "Product Title"}</h2>

        {/* Price */}
        <p style={styles.price}>
          {product.price 
            ? `$${product.price.toFixed(2)} USD` 
            : "$0.00 USD"}
        </p>

        {/* Color Picker */}
        <div style={styles.colorContainer}>
          {colors.map((color) => (
            <button
              key={color.value}
              onClick={() => setSelectedColor(color)}
              style={{
                ...styles.colorButton,
                backgroundColor: color.hex,
                border: color.value === selectedColor.value 
                  ? "2px solid #333" 
                  : "1px solid #ccc"
              }}
            >
              {color.label}
            </button>
          ))}
        </div>

        {/* Popularity Score */}
        <div style={styles.popularity}>
          {product.popularityScoreFormatted 
            ? renderStars(parseFloat(product.popularityScoreFormatted))
            : renderStars(0)}
        </div>

      </div>
    </div>
  );
}

const styles = {
  sliderContainer: {
    display: 'flex',
    alignItems: 'center',
    gap: '1rem',
    padding: '0 1rem',
  },
  gridContainer: {
    display: "grid",
    gridTemplateColumns: "repeat(4, 1fr)",
    gap: "1.5rem",
    flex: 1,
  },
  card: {
    border: "1px solid #ddd",
    borderRadius: 8,
    padding: "1rem",
    textAlign: "center",
    fontFamily: "Avenir, sans-serif"
  },
  image: {
    width: "100%",
    height: "auto",
    objectFit: "cover",
    marginBottom: "1rem"
  },
  info: {
    marginTop: "0.5rem"
  },
  title: {
    fontSize: 18,
    marginBottom: 4
  },
  price: {
    fontSize: 16,
    fontWeight: "bold",
    marginBottom: 8
  },
  colorContainer: {
    display: "flex",
    justifyContent: "center",
    gap: "0.5rem",
    marginBottom: 8
  },
  colorButton: {
    cursor: "pointer",
    borderRadius: 4,
    padding: "0.25rem 0.5rem",
    color: "#000"
  },
  popularity: {
    fontSize: 14,
    marginTop: 8
  },
  starsContainer: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    gap: '4px',
    marginTop: 8
  },
  star: {
    width: '20px',
    height: '20px',
    filter: 'invert(70%) sepia(85%) saturate(1000%) hue-rotate(359deg) brightness(105%)', // Makes stars gold colored
  },
  navButton: {
    padding: '1rem',
    fontSize: '1.5rem',
    backgroundColor: '#f0f0f0',
    border: 'none',
    borderRadius: '50%',
    cursor: 'pointer',
    width: '50px',
    height: '50px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    ':hover': {
      backgroundColor: '#e0e0e0',
    },
    ':disabled': {
      backgroundColor: '#ccc',
      cursor: 'not-allowed',
    },
  },
};

export default ProductList;
