import React, { useEffect, useState } from "react";

function ProductList() {
  const [products, setProducts] = useState([]);
  // This assumes your FastAPI server is running locally at port 8000
  const API_URL = "http://127.0.0.1:8080/products";

  useEffect(() => {
    fetch(API_URL)
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.error("Error fetching products:", err));
  }, []);

  return (
    <div style={styles.gridContainer}>
      {products.map((product) => (
        <ProductCard key={product.name} product={product} />
      ))}
    </div>
  );
}

function ProductCard({ product }) {
  // A local state to track which color is selected
  // Adjust to your actual color keys if your JSON uses "red", "blue", "green", etc.
  const colors = [
    { label: "Yellow Gold", value: "yellow", hex: "#E6CA97" },
    { label: "White Gold", value: "white", hex: "#D9D9D9" },
    { label: "Rose Gold", value: "rose", hex: "#E1A4A9" }
  ];
  const [selectedColor, setSelectedColor] = useState(colors[0]);

  const renderStars = (score) => {
    return (
      <div style={styles.starsContainer}>
        {[...Array(5)].map((_, index) => {
          const isFilled = index < Math.floor(score);
          const isHalf = !isFilled && index === Math.floor(score) && score % 1 >= 0.5;
          
          return (
            <div key={`star-${index}`} style={styles.starWrapper}>
              {/* Base star (outline) */}
              <img
                src="https://raw.githubusercontent.com/FortAwesome/Font-Awesome/master/svgs/regular/star.svg"
                alt="star outline"
                style={styles.starBase}
              />
              {/* Filled overlay */}
              {(isFilled || isHalf) && (
                <img
                  src="https://raw.githubusercontent.com/FortAwesome/Font-Awesome/master/svgs/solid/star.svg"
                  alt="star fill"
                  style={{
                    ...styles.starFill,
                    clipPath: isHalf ? 'inset(0 50% 0 0)' : 'none'
                  }}
                />
              )}
            </div>
          );
        })}
      </div>
    );
  };

  // Attempt to map the product images based on color, if your JSON has corresponding keys
  // If you have images = { yellow: "...", white: "...", rose: "..." }, then do something like:
  // const productImage = product.images[selectedColor.value];
  // For simplicity, if your JSON is "red/blue/green", adapt the color array to match.
  const productImage = product.images?.[selectedColor.value] 
                        || Object.values(product.images || {})[0]; // fallback if color key not found

  return (
    <div style={styles.card}>
      <img 
        src={productImage || "https://via.placeholder.com/200"} 
        alt={product.name} 
        style={styles.image} 
      />

      <div style={styles.info}>
        <h2 style={styles.title}>{product.name || "Product Title"}</h2>

        {/* Price */}
        <p style={styles.price}>
          {product.price 
            ? `$${product.price.toFixed(2)} USD` 
            : "$0.00 USD"}
        </p>

        {/* Color Picker */}
        <div style={styles.colorContainer}>
          {colors.map((color) => (
            <button
              key={color.value}
              onClick={() => setSelectedColor(color)}
              style={{
                ...styles.colorButton,
                backgroundColor: color.hex,
                border: color.value === selectedColor.value 
                  ? "2px solid #333" 
                  : "1px solid #ccc"
              }}
            >
              {color.label}
            </button>
          ))}
        </div>

        {/* Popularity Score */}
        <p style={styles.popularity}>
          {product.popularityScoreFormatted 
            ? `${product.popularityScoreFormatted}/5` 
            : "N/A"}
        </p>

      </div>
    </div>
  );
}

const styles = {
  gridContainer: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
    gap: "1.5rem"
  },
  card: {
    border: "1px solid #ddd",
    borderRadius: 8,
    padding: "1rem",
    textAlign: "center",
    fontFamily: "Avenir, sans-serif"
  },
  image: {
    width: "100%",
    height: "auto",
    objectFit: "cover",
    marginBottom: "1rem"
  },
  info: {
    marginTop: "0.5rem"
  },
  title: {
    fontSize: 18,
    marginBottom: 4
  },
  price: {
    fontSize: 16,
    fontWeight: "bold",
    marginBottom: 8
  },
  colorContainer: {
    display: "flex",
    justifyContent: "center",
    gap: "0.5rem",
    marginBottom: 8
  },
  colorButton: {
    cursor: "pointer",
    borderRadius: 4,
    padding: "0.25rem 0.5rem",
    color: "#000"
  },
  popularity: {
    fontSize: 14,
    marginTop: 8
  },
  starsContainer: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    gap: '4px',
    marginTop: 8
  },
  starWrapper: {
    position: 'relative',
    width: '20px',
    height: '20px',
  },
  starBase: {
    position: 'absolute',
    width: '100%',
    height: '100%',
    filter: 'invert(70%) sepia(85%) saturate(1000%) hue-rotate(359deg) brightness(105%)',
  },
  starFill: {
    position: 'absolute',
    width: '100%',
    height: '100%',
    filter: 'invert(70%) sepia(85%) saturate(1000%) hue-rotate(359deg) brightness(105%)',
  }
};

export default ProductList;
